mapper.select:
SELECT 

FROM sys_employee a

WHERE a.id = #{id}

SELECT

FROM sys_employee a

WHERE a.code = #{code}
 AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM sys_employee a
WHERE a.del_flag= #{DEL_FLAG_NORMAL}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' CONCAT('%', #{code}, '%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
AND a.ename LIKE '%'||#{eName}||'%' '%'+#{eName}+'%' CONCAT('%', #{eName}, '%')
AND a.org_id = #{org.id}
ORDER BY ${page.orderBy}
ORDER BY a.id

SELECT
FROM sys_employee a
WHERE a.del_flag= #{DEL_FLAG_NORMAL}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' CONCAT('%', #{code}, '%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
AND a.ename LIKE '%'||#{eName}||'%' '%'+#{eName}+'%' CONCAT('%', #{eName}, '%')
AND a.org_id = #{org.id}
ORDER BY ${page.orderBy}
ORDER BY a.id

mapper.update:
UPDATE sys_employee SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE sys_employee SET
	name = #{name},
	ename = #{eName},
	tel = #{tel},
	email = #{email}
WHERE id= #{id}
