mapper.select:
SELECT 
FROM sys_job a
inner join sys_role b on a.role_id = b.id
WHERE a.is_enabled= #{DEL_FLAG_NORMAL}

    AND a.role_id = #{role.id}


    AND a.job_name LIKE CONCAT('%',#{jobName},'%')

mapper.update:
UPDATE sys_job
SET role_id = #{role.id}
    ,job_name = #{jobName}
    ,job_commend = #{jobCommend}
WHERE id=#{id}

mapper.delete:
UPDATE sys_job
SET is_enabled = #{DEL_FLAG_DELETE}
WHERE id=#{id}
