mapper.select:
SELECT

FROM sys_menu a

WHERE a.id = #{id}

SELECT

FROM sys_menu a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	and a.level = #{level}


	and a.level >= #{startLevel}


	and a.level <= #{endLevel}

ORDER BY a.seq

SELECT DISTINCT

FROM sys_menu a
LEFT JOIN sys_menu p ON p.id = a.parent_id
JOIN sys_rolemenu rm ON rm.menu_id = a.id
JOIN sys_role r ON r.id = rm.role_id
JOIN sys_userrole ur ON ur.role_id = r.id
JOIN sys_user u ON u.id = ur.user_id AND u.id = #{userId}
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND r.del_flag = #{DEL_FLAG_NORMAL} AND u.del_flag = #{DEL_FLAG_NORMAL}

	and a.level = #{level}


	and a.level >= #{startLevel}


	and a.level <= #{endLevel}

ORDER BY a.seq

mapper.update:
UPDATE sys_menu SET 
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_menu SET 
	seq = #{seq}
WHERE id = #{id}
